$(document).ready(function(){$(".toggler").each(function(){$(this).click(function(){var b=$(this).attr("id");var a=b+"Cont";$("#"+a).slideToggle("fast");$(".toggableCont").each(function(){if($(this).attr("id")!=a){$(this).slideUp("fast")}})})});$(".saveText").each(function(){$(this).click(function(){var a=$(this).attr("id");newText=CKEDITOR.instances["textarea-"+a].getData();button=$(this);$.ajax({method:"POST",url:"./modul/glossar/editText.php",data:{textId:a,newText:newText},success:function(b){if(b){$("#errorText").html(b);$("#errorAlert").slideDown("fast")}else{CKEDITOR.remove("textarea-"+a);$("#successText").html(translate[101]);$("#successAlert").slideDown("fast").delay(1300).slideUp("fast");button.slideUp("fast",function(){$(".textContentSpace").each(function(){if($(this).attr("id")==a){$(this).html(newText)}});$(".editText").each(function(){if($(this).attr("id")==a){$(this).slideDown("fast")}})})}}})})});$(".editText").each(function(){$(this).click(function(){var a=$(this).attr("id");$(this).slideUp("fast",function(){$(".saveText").each(function(){if($(this).attr("id")==a){$(this).slideDown("fast")}})});$(".textContentSpace").each(function(){if($(this).attr("id")==a){textContent=$(this).html();$(this).fadeOut("fast",function(){$(this).html("<br/><textarea id='textarea-"+a+"'>"+textContent+"</textarea>");CKEDITOR.replace("textarea-"+a);$(this).fadeIn("fast")})}})})})});