$(document).ready(function(){$("#sendContact").click(function(f){f.preventDefault();$(this).prop("disabled",true);var d=$("#email").val();var g=$("#fname").val();var b=$("#lname").val();var a=$("#subj").val();var e=$("#message").val();var c="";if(!d){c+="<li>Please Enter a valid E-Mail</li>"}if(!g){c+="<li>Please Enter your Firstname</li>"}if(!b){c+="<li>Please Enter your Lastname</li>"}if(!a){c+="<li>Please choose a Subject</li>"}if(c){alert(c);$(this).prop("disabled",false)}else{$.ajax({type:"POST",data:{email:d,fname:g,lname:b,subj:a,message:e},url:"modul/kontakt/sendForm.php",success:function(h){if(h){alert(h);$(this).prop("disabled",false)}else{$(".formArea").slideUp("slow",function(){$(".alert-success").slideDown("slow")})}}})}})});