function search(){$(".groupContent").each(function(){$(this).slideDown("slow")});var d,g,f,b,c,e;d=document.getElementById("searchInput");g=d.value.toUpperCase();f=document.getElementById("searchList");b=f.getElementsByClassName("searchRow");for(e=0;e<b.length;e++){c=b[e].getElementsByClassName("searchFor")[0];if(c.innerHTML.toUpperCase().indexOf(g)>-1){b[e].style.display=""}else{b[e].style.display="none"}}}function toggleGroup(a){$(".groupContent").each(function(){if($(this).attr("groupID")==a){$(this).slideToggle("fast")}})}function toggleUser(a){$(".userContent").each(function(){if($(this).attr("userID")==a){$(this).slideToggle("fast")}})}function toggleCycle(a,b){$(".loading").each(function(){$(this).slideDown("fast")});$(".cycleContent").each(function(){if($(this).attr("userID")==a&&$(this).attr("cycleID")==b){var c=$(this);$(c).slideToggle("fast");$.ajax({method:"POST",url:"./modul/leistungslohn/call/createContent.php",data:{userID:a,cycleID:b},success:function(d){if(d){$(".loading").each(function(){$(this).slideUp("slow")});$(c).html(d)}else{$(".loading").each(function(){$(this).slideUp("slow")});$(c).html($translate[157])}}})}})}function toggleSemester(a,b){$(".semesterContent").each(function(){if($(this).attr("userID")==a&&$(this).attr("semesterID")==b){$(this).slideToggle("fast")}})}function toggleCycleExam(a,b){$(".cycleContentExam").each(function(){if($(this).attr("userID")==a&&$(this).attr("cycleID")==b){$(this).slideToggle("fast")}})}$(document).ready(function(){$("#openExporter").click(function(){$("#exporterContents").load("./modul/leistungslohn/exporter.php")})});