$(document).ready(function(){$(".openReg").each(function(){$(this).click(function(a){a.preventDefault();var b=$(this).attr("group");var c=$(this).attr("lang");$(".toggableReg").each(function(){if($(this).attr("lang")==c&&$(this).attr("group")==b){$(this).slideToggle("fast")}else{$(this).slideUp("fast")}})})})});